        if self.scores["update_freq"] > 7:
            recommendations.append({
                "category": "데이터 파이프라인",
                "priority": "높음",
                "title": "실시간/준실시간 업데이트 파이프라인",
                "details": [
                    "스트리밍 데이터 처리 (Kafka, Kinesis)",
                    "증분 임베딩 업데이트",
                    "벡터 DB 실시간 동기화",
                    "캐시 무효화 전략"
                ]
            })

        # 데이터 형식 기반 권장사항
        if self.profile.data_format == DataFormat.CODE:
            recommendations.append({
                "category": "데이터 처리",
                "priority": "중간",
                "title": "코드 특화 처리",
                "details": [
                    "코드 파싱 및 AST 분석",
                    "함수/클래스 단위 청킹",
