                "name": "text-embedding-3-large 또는 Cohere embed-v3",
                "reason": "범용성, 성능 우수"
            }

        # 오케스트레이션
        if self.scores["update_freq"] > 7:
            infra["orchestration"] = {
                "name": "Apache Airflow 또는 Prefect",
                "reason": "실시간/스케줄링 워크플로우 관리"
            }

        return infra

    def _estimate_timeline(self) -> Dict:
        """타임라인 추정"""
        base_weeks = 4

        # 복잡도 요인
        if self.scores["data_size"] > 10:
            base_weeks += 2
        if self.scores["ethics"] > 7:
            base_weeks += 2
        if self.scores["domain_spec"] > 7:
            base_weeks += 1

        return {
            "phase1_diagnosis": "1주",
            "phase2_data_prep": f"{min(3, base_weeks // 2)}주",
            "phase3_rag_build": f"{min(3, base_weeks // 2)}주",
